train_df.drop('PassengerId', axis=1, inplace=True)
train_df.drop('Name', axis=1, inplace=True)
train_df.drop('Ticket', axis=1, inplace=True)

train_df.head()

# เนื่องจากข้อมูลเป็น boolean (True/False) จึงทำการแปลงเป็น int (1/0)
boolean_cols = ["Pclass_1", "Pclass_2", "Pclass_3", "Sex_female", "Sex_male", "Embarked_C", "Embarked_Q", "Embarked_S"]
train_df[boolean_cols] = train_df[boolean_cols].astype(int)

